import React, { useRef, useEffect, useState, Suspense, useThree } from "react";
import "./App.scss";
//Components
import Header from "./components/header";
import { Section } from "./components/section";

// Page State
import state from "./components/state";

// R3F
import { Canvas, useFrame } from "react-three-fiber";
import { Html, useProgress, useGLTFLoader } from "drei";

// React Spring
import { a, useTransition } from "@react-spring/web";
//Intersection Observer
import { useInView } from "react-intersection-observer";

function Model({ modelPath }) {
  const gltf = useGLTFLoader(modelPath, true);
  return <primitive object={gltf.scene} dispose={null} />;
}

const { camera, size: { width, height } } = useThree();
const aabb = new THREE.Box3().setFromObject(someObject3D);

camera.zoom = Math.min(
  width / (aabb.max.x - aabb.min.x),
  height / (aabb.max.y - aabb.min.y)
);
camera.updateProjectionMatrix();

const Lights = () => {
  return (
    <>
      {/* Ambient Light illuminates lights for all objects */}
      <ambientLight intensity={0.3} />
      {/* Diretion light */}
      <directionalLight position={[10, 10, 5]} intensity={1} />
      <directionalLight
        castShadow
        position={[0, 10, 0]}
        intensity={1.5}
        shadow-mapSize-width={1024}
        shadow-mapSize-height={1024}
        shadow-camera-far={50}
        shadow-camera-left={-10}
        shadow-camera-right={10}
        shadow-camera-top={10}
        shadow-camera-bottom={-10}
      />
      {/* Spotlight Large overhead light */}
      <spotLight intensity={1} position={[1000, 0, 0]} castShadow />
    </>
  );
};

const HTMLContent = ({
  children,
  modelPath,
  positionY,
  domContent
}) => {
  const ref = useRef()
  // useFrame(() => (
  //   ref.current.rotation.y -= 0.01
  // ))
  return (
    // <Section factor={1.5} offset={1}>
    //   <group position={[0, position, 0]}>
    //     <mesh ref={ref} position={[0, -35, 0]}>
    //       <Model url={modelPath} />
    //     </mesh>
    //     <Html fullscreen portal={domContent}>
    //       <div ref={refItem} className='container'>
    //         <h1 className='title'>{children}</h1>
    //       </div>
    //     </Html>
    //   </group>
    // </Section>
    <Section factor={1.5} offset={1}>
      <group position={[0, positionY, 0]}>
      <mesh ref={ref} position={[0, -35, 0]}>
        <Model modelPath={modelPath}/>
        </mesh>
        <Html portal={domContent} fullscreen>
          {children}
        </Html>
      </group>
    </Section>
  );
};

export default function App() {
  const domContent = useRef()

  const scrollArea = useRef();
  const onScroll = (e) => (state.top.current)
  return (
    <>
      <Header />
      <Canvas colorManagement camera={{ position: [0, 0, 120], fov: 70 }}>
        {/* Lights Component */}
        <Lights/>
        <Suspense fallback={null}>
          <HTMLContent 
            modelPath='/scene.gltf'
            positionY={250}
            domContent={domContent}
          >
            <div className="container">
              <h1 className="title">Hello</h1>
            </div>
          </HTMLContent>
        </Suspense>

      </Canvas>

      <div className="scrollArea">
          <div style={{position: 'sticky', top:0}} ref={domContent}></div>
          <div style={{height: `${state.pages * 100}vh`}}></div>
      </div>
    </>
  );
}
